import React from 'react';
import { Link } from 'react-router-dom';

import ReactQuill from 'react-quill';

import './form.css'
import 'react-quill/dist/quill.snow.css';
// import { notDeepEqual } from 'assert';

export default class NewPage extends React.Component {
    constructor(props) {
        super(props);

        this.state={
            note: {
                title:'',
                body:'',
                createdAt:undefined,
                updatedAt:undefined
            },
            saving: false
        }
        this.handleBodyChange = this.handleBodyChange.bind(this);
    }

    handleBodyChange(value) {
        const { note } = this.state;
        this.setState({
            note: { ...note, body: value}
         })
    }

    updateValue = (e) => {
        const { note } = this.state;
        this.setState({
            note: { ...note, [e.target.name]: e.target.value}
        });
    }

    handleSave = async(e) => {
        e.preventDefault();

        if(!this.state.note.title){
            console.log("must provide title")
            return
        }
        console.log("saving this", this.state.note)
        const id = await this.props.onSave(this.state.note);
        this.props.history.replace(`/notes/${id}`);
    }

    render(){
        const { note } = this.state;

        return (
            <div className="note-form">
                <h1>New Note</h1>
                <form onSubmit={this.handleSave}>
                    <div className="note-form-field">
                        <label>Title</label>
                        <input type="text" name="title" value={note.title} onChange={this.updateValue} />
                    </div>
                    <div className="note-form-field note-form-field-text">
                        {/* <textarea name="body" value={note.body} onChange={this.updateValue}/> */}
                        <ReactQuill theme="snow" value={note.body} onChange={this.handleBodyChange} />
                    </div>
                    <div className="note-form-buttons">
                        <button className="btn">Save</button>
                        <Link to={`/`}>Cancel</Link>
                    </div>
                </form>
            </div>
        );
    }
}
