import React from 'react';
import { Link } from 'react-router-dom';
import marked from 'marked';
import dayjs from 'dayjs';
import ReactDOM from 'react-dom';
import { WithContext as ReactTags } from 'react-tag-input';

import './show.css';

export default class ShowPage extends React.Component {
    constructor(props) {
        super(props);

        this.state={
            tags:[],
            note: {
                title:'',
                body:'',
                createdAt:undefined,
                updatedAt:undefined,
                tags:[]
            },
            saving: false
        }
        //React Tags Linking
        // this.handleDeleteTag = this.handleDeleteTag.bind(this);
        // this.handleAdditionTag = this.handleAdditionTag.bind(this);
    }

    renderDate() {
        let d = dayjs(this.props.note.updatedAt)
        return d.format("MMMM D YYYY, HH:mm")
    }

    componentWillMount() {
        if(!this.props.note) {
            this.props.history.replace(`/`);
            return;
        }
    }

    render(){
        const {note} = this.props;

        if(!note) { return null; }

        return (
            <div>
                <h1>{note.title}</h1>
                <div className="note-created">
                    {this.renderDate()}
                    <Link className="note-edit" to={`/notes/${note._id}/edit`}>Edit</Link>
                    <button className="btn" onClick={ (e) => this.props.onDelete(note._id)}>Delete</button>
                </div>
                <div className="note-body" dangerouslySetInnerHTML={ {__html: marked(note.body) } } />
            </div>
        );
    }
}
